events.inlineCode:
//Pega o numerador calculado
var numD = runtimeScene.getVariables().get("numD");
var numDc = numD.getValue();

//Pega o numerador calculado
var numDeo = runtimeScene.getVariables().get("denominador");
var numDe = numDeo.getValue();

if(numDc != 0){
    while (numDc % 2 != 1) {
        numDc = numDc / 2;
        numDe = numDe / 2;
    }; // e
    numD.setValue(numDc);
    numDeo.setValue(numDe);
}
